def makeSet(n):
  global parent, ranks
  parent = [i for i in range (1000 ** 2 + 1)]
  ranks = [0 for i in range(1000 ** 2 + 1)]


def findSet(u):
  if u != parent[u]:
    parent[u] = findSet(parent[u])
  return parent[u]

def unionSet(u, v):
  up = findSet(u)
  vp = findSet(v)
  if up == vp:
    return
  if ranks[up] > ranks[vp]: 
    parent[vp] = up
  elif ranks[up] < ranks[vp]:
    parent[up] = vp
  else:
    parent[up] = vp
    ranks[vp] += 1


def getID(x, y):
  return x * n + y


