def find_num_sections(arr, index):
	count = 0
	ceiling = arr[index]
	for i in range(index, -1, -1):
		if(arr[i] <= ceiling):
			ceiling = arr[i]
			count += 1
		else:
			break
	ceiling = arr[index]
	for i in range(index+1, len(arr)):
		if(arr[i] <= ceiling):
			ceiling = arr[i]
			count += 1
		else:
			break
	return count

