long long expo(int x , int n)
{
    long long res;
    if(n == 0)
    {
        return 1;
    }
    if(!(n & 1))
    {
        res = expo(x , n / 2);
        return res * res;
    }
    else
    {
        res = expo(x , (n - 1) / 2);
        return x * res * res;
    }
}


