//		printf("status %d\n",status);
		if(status == 1){
			if(jj-ii==size && jj<=M+1){
				counter++;
				map[counter][0]=ii;
				map[counter][1]=size;
				for(iii=ii;iii<jj;iii++) memory[iii]=1;
				printf("%d\n",counter);
				return;
			}
		}else{

		}
	}
	printf("NULL\n");
}

void hapus(int idx){
	if(idx<1 || idx >100 || map[idx][0]==-1){
		printf("ILLEGAL_ERASE_ARGUMENT\n");
	}else{
		int ii, batas = map[idx][0] + map[idx][1];
		for(ii=map[idx][0]; ii<batas; ii++) memory[ii]=0;
		map[idx][0]=-1;
		map[idx][1]=-1;
	}
}

void changeMap(int minIdx, int moves){
	int i;
	for(i=1;i<=100;i++){
		if(map[i][0]>=minIdx){
			map[i][0]-=moves;
		}
	}
}

void fragment(){
	int i, j, k, count=0, batas;
	for(i=1; i<=M; i++){
		if(memory[i]==0){
			j=i;
			while(j<=M && memory[j]==0) j++;
			if(j<=M){
				batas = j-i;
				changeMap(j, batas);
				for(k=i; j<=M; k++){
					memory[k] = memory[j];
					memory[j] = 0;
					j++;
				}	
			}			
		}
	}
}

