#include <stdio.h>
#include <stdbool.h>
#include <memory.h>
#define N 1000

bool dfs(int i, const int incid[2][N][N+1], bool whatta[2][N]) {
	int j, k;
	if (whatta[0][i])
		return false;
	whatta[0][i] = true;
	for (j = 1; j <= incid[0][i][0]; j ++) {
		int jj = incid[0][i][j];
		if (whatta[1][jj])
			continue;
		whatta[1][jj] = true;
		for (k = 1; k <= incid[1][jj][0]; k ++) {
			int kk = incid[1][jj][k];
			(void) dfs(kk, incid, whatta);
		};
	};
	return true;
};

int main(void) {
	int incid[2][N][N+1];
	bool whatta[2][N];
	int ctx;
	int n, i, j, k;
	memset(incid, 0, sizeof incid);
	memset(whatta, 0, sizeof whatta);
	scanf("%d", &n);
	for (i = 0; i < n; i ++) {
		scanf("%d%d", &j, &k); --j; --k;
		incid[0][j][++incid[0][j][0]] = k;
		incid[1][k][++incid[1][k][0]] = j;
	};
	ctx = 0;
	for (i = 0; i < N; i ++)
		if (incid[0][i][0])
			if (dfs(i, incid, whatta))
				++ctx;
	--ctx;
	printf("%d\n", ctx);
	return 0;
};

