void QuickSort(int *array, int from, int to)
{
        if(from>=to)return;
        int pivot = array[from];
        int i = from, j, temp;
        for(j = from + 1;j <= to;j++)
        {
                if(array[j] < pivot) 
                {
                        i = i + 1;
                        temp = array[i];
                        array[i] = array[j];
                        array[j] = temp;
                }
        }
        temp = array[i];
        array[i] = array[from];
        array[from] = temp;
        QuickSort(array,from,i-1);
        QuickSort(array,i+1,to);
}

