        cost = 0;
        same = samenum[i];

        for (p = 1; p < 10; p++) {
            if (i + p >= 0 && i + p <= 9) {
                for (j = 1; j <= ind[i + p][0] && same < k; j++) {
                    cost += abs(res[ind[i + p][j]] - '0' - i);
                    res[ind[i + p][j]] = '0' + i;
                    same++;
                }
            }
            if (i - p >= 0 && i - p <= 9) {
                for (j = ind[i - p][0]; j >= 1 && same < k; j--) {
                    cost += abs(res[ind[i - p][j]] - '0' - i);
                    res[ind[i - p][j]] = '0' + i;
                    same++;
                }
            }
        }
        if ((minimum > cost) || (minimum == cost && strcmp(result, res) > 0)) {
            strcpy(result, res);
            minimum = cost;
