#include <stdio.h>
#include <string.h>
#include <ctype.h>

#define L 10010
#define D 10
#define INF 0x3f3f3f3f

int n, m;
char num[L];
int best_cost;
char best_result[L];

void solve(int e) {
  int i, d, j, k, cost;
  static char result[L];
  static int idx[D][L], len[D];

  memset(len, 0, sizeof len);
  for (i = 0; i < n; i++) {
    d = num[i] - '0';
    k = abs(d - e);
    if (d > e) idx[k][len[k]++] = i;
  }
  for (i = n - 1; i >= 0; i--) {
    d = num[i] - '0';
    k = abs(d - e);
    if (d <= e) idx[k][len[k]++] = i;
  }

  k = m;
  cost = 0;
  strcpy(result, num);
  for (i = 0; k && i < 10; i++)
    for (j = 0; k && j < len[i]; j++, k--) {
      cost += i;
      result[idx[i][j]] = '0' + e;
    }
  if (cost < best_cost || cost == best_cost && strcmp(result, best_result) < 0) {
    best_cost = cost;
    strcpy(best_result, result);
  }
}

int main() {
  int e;

  scanf("%d %d", &n, &m);
  scanf("%s", num);
  best_cost = INF;
  for (e = 0; e < 10; e++)
    solve(e);
  printf("%d\n", best_cost);
  printf("%s\n", best_result);

  return 0;
}

