            ms[0].first += v;
            ms[1].first += v;
            ms[2].first += v;
            tmp += v;
        }else{
            if(tmp){
                e->add(e->x,e->y,tmp);
                d->add(d->x,d->y,tmp);
                tmp = 0;
            }
            if(a <= e->y) e->add(a,min(e->y,b),v);
            if(b >= d->x) d->add(max(a,d->x),b,v);

