int N;

struct Node {
    int id;
    Node *next[52];
} node[MAXN*MAXN], *root;
int n_tot;

int mark[MAXN];

int main_ ()
{
    scanf ("%d\n", &N);
    int l0, l1;
//-4: (; -3: ); -2: inputing token; -1: null; 0: c; 1~4: +,-,*,/
    root = node; n_tot = 1;
