        conf[p]-=a*j; conf[p-1]-=b*j; conf[p+1]-=b*j;
        int curr = j+calc(conf,p+1);
        if (curr<ans) { ans=curr; best[p1+LAST][p2+LAST][p]=j; }
        conf[p]+=a*j; conf[p-1]+=b*j; conf[p+1]+=b*j;
        }
    }
    return memo[conf[p-1]+LAST][conf[p]+LAST][p]=ans;
}

void solve()
{
    memset(memo,-1,sizeof memo);
    memset(best,0,sizeof best);
    cin >> n >> a >> b;
