    int k=best[p1][p2][1];
    for(int i=0; i<k; ++i) ans.PB(2);
    h[0]-=b*k; h[1]-=a*k; h[2]-=b*k;

    for(int i=2; i<n-1; ++i) {
        k = best[h[i-1]+LAST][h[i]+LAST][i]; h[i-1]-=b*k; h[i]-=a*k; h[i+1]-=b*k;
        for(int j=0; j<k; ++j) ans.PB(i+1);
        kans -= k;
    }
    for(int i=0; i<sz(ans); ++i) {
        if (i) cout << ' ';
        cout << ans[i];
    }
    cout << endl;
}

int main()
