#include<cstdio>
#include<cstring>
#define maxn 100005
char s1[maxn],s2[maxn],s[maxn],ss[maxn];
int next1[maxn],next2[maxn],len,len1,len2;
int kmp(char *des,char *s,int *next)
{
    int i,j=-1;
    for(i=0;des[i];i++)
    {
        while(s[j+1]!=des[i]&&j>=0) j=next[j];
        if(s[j+1]==des[i]) j++;
        if(s[j+1]==0) return i;
    }
    return -1;
}
void getnext(char *s,int *next)
{
    int i,j=-1;
    next[0]=-1;
    for(i=1;s[i];i++)
    {
        while(j>=0&&s[j+1]!=s[i]) j=next[j];
        if(s[j+1]==s[i]) j++;
        next[i]=j;
    }
}
int main()
{
    scanf("%s%s%s",s,s1,s2);
    len1=strlen(s1);
    len2=strlen(s2);
    len=strlen(s);
    getnext(s1,next1);
    getnext(s2,next2);
    bool f1=0,f2=0;
    int x1=kmp(s,s1,next1),x2;
    if(x1>=0)
    {
        x2=kmp(s+x1+1,s2,next2);
        if(x2>=0) f1=1;
    }
    for(int i=0;i<len;i++) ss[i]=s[len-1-i];
    x1=kmp(ss,s1,next1);
    if(x1>=0)
    {
        x2=kmp(ss+x1+1,s2,next2);
        if(x2>=0) f2=1;
    }
    if(f1==0&&f2==0) puts("fantasy");
    else if(f1==0) puts("backward");
    else if(f2==0) puts("forward");
    else puts("both");
    return 0;
}

