using namespace std;

int vis[100010];
long long int cnt[100010];
int CC[100010];
vector<int> V[100010];
int cc=1;

void dfs(int cur) {
	if (vis[cur]) return ;
	vis[cur]=1;
	int l=V[cur].size();
	for (int i=0;i<l;i++) {
		CC[V[cur][i]]=cc;
		dfs(V[cur][i]);
	}
}

int main()
{
	memset(vis,0,sizeof vis);
	memset(cnt,0,sizeof cnt);
	long long int n,m,mod;
	cin >> n >> m >> mod;
	for(int i=0;i < m; i++)
	{
		int x,y;
		cin >> x >> y;
		V[x].push_back(y);
		V[y].push_back(x);
	}
	for (int i=1;i<=n;i++) {
		if (!vis[i]) {
			CC[i]=cc;
			dfs(i);
			cc++;
		}
	}
	for (int i=1;i<=n;i++) {
		cnt[CC[i]]++;
	}
	int N = 0;
	long long int val = 1;
	for (int i=1;i<=n;i++) {
		if (cnt[i]) {
			N++;
			val=(val*cnt[i])%mod;
		}
	}
	if (N==1) cout << 1%mod << endl;
	else {
		for (int i=N-2;i>0;i--) val=(val*n)%mod;
		cout << val << endl;
	}
	return 0;
}

