#define FORN(X,Y) for (int (X) = 0;(X) < (Y);++(X))
#define DEBUG(x) cout << '>' << #x << ':' << x << '\n';

#define REP(X,Y,Z) for (int (X) = (Y);(X) < (Z);++(X))
#define RESET(Z,Y) memset(Z,Y,sizeof(Z))

#define SZ(Z) ((int)Z.size())
#define ALL(W) W.begin(), W.end()
#define PB push_back

#define MP make_pair
#define A first
#define B second

#define INF 1023123123
#define EPS 1e-11

#define MX(Z,Y) Z = max((Z),(Y))
#define MN(X,Y) X = min((X),(Y))

#define FORIT(X,Y) for(typeof((Y).begin()) X = (Y).begin();X!=(Y).end();X++)

using namespace std;

typedef long long ll;
typedef double db;
typedef vector<int> vint;
typedef vector<ll> vll;

//O(n log n)
vector<int> SequenceSimplify(vector<int> seq) {
	int lowest = 0;
	vector<int> disort = seq;
	sort(ALL(disort));
	disort.erase(unique(ALL(disort)),disort.end());
	FORN(i,SZ(seq)) {
		seq[i] = (lower_bound(ALL(disort),seq[i]) - disort.begin()) + lowest;
		}
	return seq;
	}

//vint a = {10, 50, 5, 50, 10, 70}
//SequenceSimplify(a) = {1, 2, 0, 2, 1, 3}

ll modu = 1000000007LL;

//ONE indexed
struct FenwickTree {
	int n;
	ll bit[400005];
	FenwickTree (int _n) {
		n = _n;
		FORN(i,n) bit[i+1] = 0;
		}
	void add (int pos, ll val) {
		while (pos <= n) {
			bit[pos] += val;
      bit[pos] %= modu;
			pos += (pos & -pos);
    }
  }
	ll sum(int ending) {
		if (ending > n) ending = n;
		ll retval = 0;
		while (ending >= 1) {
			retval += bit[ending];
			ending -= (ending & -ending);
    }
		return retval % modu;
  }

	ll sumarea(int mulai, int selesai) {
		if (mulai > selesai) return 0LL;
		return (sum(selesai) - sum(mulai - 1) + modu) % modu;
  }
};

int main() {

  int n, m;
  cin >> n >> m;

  int target_ok = 0;
  int awal_ok = 0;

  vector<int> input;
  FORN(i, m) {
    int dari, ke;
    scanf("%d %d", &dari, &ke);
    input.PB(dari);
    input.PB(ke);
    if (ke == n) target_ok = 1;
    if (dari == 0) awal_ok = 1;
  }

  if (!target_ok || !awal_ok) {
    cout << 0LL << endl;
    return 0;
  }

  input = SequenceSimplify(input);
  FenwickTree tree(SZ(input)+10);

  int target = *max_element(ALL(input));

  vector< pair<int, int> > bus;
  FORN(i, m) {
    int dari, ke;
    dari = input[i*2];
    ke = input[i*2+1];
    bus.PB(MP(ke, dari));
  }

  sort(ALL(bus));

  tree.add(1, 1LL);
  FORN(i, m) {
    int dari = bus[i].B;
    int ke = bus[i].A;
    ++dari;
    ++ke;
    // ke = 3, dari = 2 itu sum dari [2, 3]
    ll jml = tree.sumarea(dari, ke-1LL);
    while (jml < 0LL) jml += modu;
    jml %= modu;
    tree.add(ke, jml);
  }

  cout << tree.sumarea(target+1, target+1) << endl;

  return 0;
}

