				if (v[0] + v[1] + v[2] < n2)
					continue;

				if (v[0] + v[1] >= n2)
				{
					ans = 0;
					break;
				}
				long ans1 = n2 - v[0] - v[1];
				if (ans1<ans)
					ans = ans1;
			}
			if (ans == long.MaxValue)
				ans = -1;
			Console.WriteLine(ans);

