namespace ConsoleApplication1
{
    class Program
    {
        int nextInt()
        {
            int c = ' ';
            while ((c == (int)' ' || c == (int)'\n' || 
                c == (int)'\t' || c == (int)'\r') 
                && c != -1)
            {
                c = Console.Read();
            }
            bool flag = false;
            if (c == '-') flag = true;
            else
                if ((c < '0' || c > '9') && c != '+') return 0;
            int res = 0;
            if (!char.IsDigit((char)c)) c = Console.Read();
            while (char.IsDigit((char)c))
            {
                res = res * 10 + (int)c - (int)'0';
                c = Console.Read();
            }
            if (flag) res = -res;
            return res;
        }

        bool[][] a;
        int[] f;
        int cur, n;

        void combine(bool[] v1, bool[]v2)
        {
            for(int i=0;i<n;i++)
            {
                v1[i]|=v2[i];
            }
        }

        bool[] flag;
        bool[,] m;

        void dfs(int v)
        {
            if (flag[v]) return;
            flag[v] = true;
            if (v < cur) combine(flag, a[v]);
            for (int i = 0; i < n; i++)
            {
                if (m[v, i]) dfs(i);
            }
        }        


        void Solve()
        {
            n = nextInt();
            f = new int[n];
            int[] p = new int[n];
            a = new bool[n][];
            flag = new bool[n];
            for (int i = 0; i < n; i++) p[i] = nextInt()-1;
            for (int i = 0; i < n; i++) f[i] = nextInt();
            m = new bool[n, n];
            for (int i = 0; i < n; i++)
            {
                if (i + f[i] < n) m[i, i + f[i]] = m[i + f[i], i] = true;
                if (i - f[i] >= 0) m[i, i - f[i]] = m[i - f[i], i] = true;
            }
            for (cur = 0; cur < n; cur++)
            {
                for (int j = 0; j < n; j++) flag[j] = false;
                dfs(cur);
                a[cur] = new bool[n];
                combine(a[cur], flag);
            }
            for (int i = 0; i < n; i++)
            {
                if (!a[p[i]][i])
                {
                    Console.Write("NO");
                    return;
                }
            }
            Console.Write("YES");
        }

        static void Main(string[] args)
        {
#if MY_SUPER_PUPER_ONLINE_JUDGE
            string strAppDir =
                        Path.GetDirectoryName(Assembly.GetExecutingAssembly().GetModules()[0].FullyQualifiedName);
            Console.SetIn(new StreamReader(strAppDir + "\\input.txt"));
            Console.SetOut(new StreamWriter(strAppDir + "\\output.txt"));
