using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;

namespace Codeforces
{
    class Program
    {
        static void Main()
        {            
            var input = Console.ReadLine().Trim().Split(' ');
            var n = Int32.Parse(input[0]);
            var m = Int32.Parse(input[1]);

            var index = new int[n][];
            var chars = new string[n];

            for (int i = 0; i < n; i++)
            {
                chars[i] = Console.ReadLine();
                index[i] = new int[m];
            }
            for (int i = 0; i < n; i++)
            {
                for (int j = 0; j < m; j++) 
                {
                    if (index[i][j] != 1)
                    {
                        for (int k = i + 1; k < n; k++)
                            if (chars[i][j] == chars[k][j])
                                index[i][j] = index[k][j] = 1;
                    }
                    if (index[i][j] != 2)
                    {
                        for (int k = j + 1; k < m; k++)
                            if (chars[i][j] == chars[i][k])
                                index[i][j] = index[i][k] = 2;
                    }
                }
            }
            var output = new List<char>();
            for (int i = 0; i < n; i++)
                for (int j = 0; j < m; j++)
                    if (index[i][j] == 0)
                        output.Add(chars[i][j]);
            Console.WriteLine(new string(output.ToArray()));

        }
    }
}

