using System;
using System.IO;
using System.Collections.Generic;

namespace CodeForces
{
    class Tokenizer
    {
        int pos;
        string[] tokens;
        TextReader reader;

        public Tokenizer(TextReader reader)
        {
            pos = 0;
            tokens = new string[] { };
            this.reader = reader;
        }


        public string NextToken()
        {
            if (pos == tokens.Length)
            {
                tokens = reader.ReadLine().Split(' ');
                pos = 0;
            }
            return tokens [pos++];
        }

        public int NextInt()
        {
            return int.Parse(NextToken());
        }
    }

    class MainClass
    {
        static Tokenizer t;
        static TextWriter w;

        public static void Init()
        {
            t = new Tokenizer(Console.In);
            w = Console.Out;
            //t = new Tokenizer(File.OpenText(@"../../in.txt"));
            //w = File.CreateText(@"../../out.txt");
        }

        public static void Main(string[] args)
        {
            Init(); 
            int n = t.NextInt();
            List<int> numbers = new List<int>();
            List<int> candidate = new List<int>();
            List<int> ans = new List<int>();
            candidate.Add(0);

            bool hasZero = false;
            for (int i = 0; i < n; i++)
            {
                numbers.Add(t.NextInt());
                if (numbers [i] == 0)
                    hasZero = true;
                if (numbers [i] % 3 == 0)
                    ans.Add(numbers [i]);
                else
                    candidate.Add(numbers [i]);
            }
            if (!hasZero)
            {
                w.WriteLine(-1);
                w.Close();
                return;
            }
            candidate.Sort();

            int c = candidate.Count;
            int[,] dp = new int[c, 3];
            int[,] prev = new int[c, 3];

            prev [0, 0] = 0;
            prev [0, 1] = prev [0, 2] = -1;

            for (int i = 1; i < c; i++)
            {
                int num = candidate [i];
                int curRem = num % 3;
                for (int target = 0; target < 3; target++)
                {
                    int reqRem = (target + 3 - curRem) % 3;
                    if (prev [i - 1, reqRem] >= 0 && 
                        1 + dp [i - 1, reqRem] >= dp [i - 1, target])
                    {
                        dp [i, target] = 1 + dp [i - 1, reqRem];
                        prev [i, target] = i;
                    } else
                    {
                        dp [i, target] = dp [i - 1, target];
                        prev [i, target] = prev [i - 1, target];
                    }
                }
            }

            int rem = 0;
            int cur = prev [c - 1, rem];
            while (cur > 0)
            {
                ans.Add(candidate [cur]);
                rem = (rem + 3 - (candidate [cur] % 3)) % 3;
                cur = prev [cur - 1, rem];
            }

            ans.Sort();
            ans.Reverse();

            if (ans.Count == 0)
            {
                w.WriteLine(-1);
                w.Close();
                return;
            }

            bool allZeros = true;
            for (int i = 0; i < ans.Count; i++)
                if (ans [i] != 0)
                    allZeros = false;

            if (allZeros)
            {
                w.WriteLine(0);
                w.Close();
                return;
            }

            for (int i = 0; i < ans.Count; i++)
            {
                w.Write(ans [i]);
            }
            w.WriteLine();
            w.Close();
        }

    }
}

