    static List<int> lst;
    static int n, k;

    static bool Check(int t)
    {
        int last = 0;
        int remain = k - 1;
        for (int i = 1; i < lst.Count; i++)
        {
            if (lst[i] - last > t + 1) return false;
            if (i == lst.Count - 1 || lst[i + 1] - last > t + 1)
            {
                last = lst[i];
                if (remain-- == 0)
                {
                    return false;
                }
            }
        }
        return true;
    }

