                    result[scores[i].Index] = 1;
                }
                for (int i = 0; i < b; i++) {
                    result[scores[i + a].Index] = 2;
                }
            } else if (a < b) {
                Array.Sort(scores, new MyComparer2());
                for (int i = 0; i < b; i++) {
                    result[scores[i].Index] = 2;
                }
                for (int i = 0; i < a; i++) {
                    result[scores[i + b].Index] = 1;
                }
            } else {
                Array.Sort(scores, new MyComparer());
                for (int i = 0; i < a; i++) {
                    result[scores[i].Index] = 1;
                }
                for (int i = 0; i < b; i++) {
                    result[scores[i + a].Index] = 2;
                }
            }
