    class C
    {
        public static void Main()
        {
            int n = int.Parse(Console.ReadLine());
            var xs = Array.ConvertAll(Console.ReadLine().Split(' '), sss => int.Parse(sss));
            var ys = Array.ConvertAll(Console.ReadLine().Split(' '), sss => int.Parse(sss));
            var yss = (int[])ys.Clone();
            int a = xs[0],
                b = xs[1];

            if (n == 2 * a)
            {
                for (int i = 0; i < a; i++)
                    Console.Write("1 ");
                for (int i = 0; i < b; i++)
                    Console.Write("2 ");
                return;
            }
            int[] fetch = new int[5];
            QuickSort(yss);
            if (n > 2 * a)
            {
                Array.Reverse(yss);
                for (int i = 0; i < a; i++)
                    fetch[yss[i] - 1]++;
                for (int i = 0; i < n; i++)
                    if (fetch[ys[i] - 1] > 0)
                    {
                        fetch[ys[i] - 1]--;
                        Console.Write("1 ");
                    }
                    else
                    {
                        Console.Write("2 ");
                    }
            }
            else
            {
                for (int i = 0; i < a; i++)
                    fetch[yss[i] - 1]++;
                for (int i = 0; i < n; i++)
                    if (fetch[ys[i] - 1] > 0)
                    {
                        fetch[ys[i] - 1]--;
                        Console.Write("1 ");
                    }
                    else
                    {
                        Console.Write("2 ");
                    }
            }
        }

        #region QuickSort
        static void Swap(int[] xs, int i, int j) { int t = xs[i]; xs[i] = xs[j]; xs[j] = t; }
        static int[] QuickSort(int[] xs) { return QuickSort(xs, 0, xs.Length); }
        static int[] QuickSort(int[] xs, int start, int count)
        {
            if (count < 2) return xs;

            Stack<int> s = new Stack<int>(),
                       c = new Stack<int>();

            s.Push(start);
            c.Push(count);

            while (s.Count > 0)
            {
                start = s.Pop();
                count = c.Pop();

                int last = start + count - 1,
                    forward = start - 1,
                    backward = start + count,
                    pivot = xs[start + count / 2];

                while (true)
                {
                    while (xs[++forward] < pivot) ;
                    while (pivot < xs[--backward]) ;
                    if (forward >= backward) break;
                    Swap(xs, forward, backward);
                }

                if (start < forward - 1)
                {
                    s.Push(start);
                    c.Push(forward - start);
                    //QuickSort(xs, start, forward - start);
                }

                if (backward + 1 < last)
                {
                    s.Push(backward + 1);
                    c.Push(last - (backward + 1) + 1);
                    //QuickSort(xs, backward + 1, last - (backward + 1) + 1);
                }
            }

            return xs;
        }
        #endregion
    }
}

